function cpac_actions_column(t){t(".column-actions .cpac_use_icons + .row-actions > span").each(function(){var c=t(this).find("a");c.attr("data-tip",c.text()).addClass("cpac-tip")})}function cpac_set_column_classes(t){for(var c in AC.column_types)if(AC.column_types.hasOwnProperty(c)){var n=AC.column_types[c];t(".wp-list-table td."+c).addClass(n)}}function cpac_tooltips(t){"undefined"!=typeof t.fn.qtip&&t(".cpac-tip").qtip({content:{attr:"data-tip"},position:{my:"top center",at:"bottom center"},style:{tip:!0,classes:"qtip-tipsy"}})}function cpac_quickedit_events(t){t(document).ajaxComplete(function(c,n,a){var i=t("<div>").append(n.responseText);if(1==i.find("tr.iedit").length){var e=i.find("tr.iedit").attr("id");t("tr#"+e).trigger("updated")}})}jQuery(document).ready(function(t){cpac_actions_column(t),cpac_tooltips(t),cpac_quickedit_events(t),cpac_set_column_classes(t)});